mapper.delete:
delete from company_year_worker


		year = #{year,jdbcType=CHAR} 


		and company_code = #{companyCode,jdbcType=CHAR} 


		and worker_id = #{workerId,jdbcType=CHAR}

mapper.select:
select

from company_year_worker as cyw
where cyw.id = #{id,jdbcType=INTEGER}

select

from company_year_worker as cyw



			cyw.year = #{companyYearWorker.year}


			and cyw.worker_id = #{companyYearWorker.workerId}


			and cyw.company_code = #{companyYearWorker.companyCode}


			#{companyYearWorker.isActive,jdbcType=BIT}, 



order by cyw.update_time desc
limit #{start},#{size}

