mapper.select:
select

from




			ac.year = #{accountsLog.year,jdbcType=CHAR}




				and c.id = #{accountsLog.company.id, jdbcType=INTEGER} 


				and c.company_code like concat('%',#{accountsLog.company.companyCode, jdbcType=VARCHAR},'%')


				and c.company_name like concat('%',#{accountsLog.company.companyName, jdbcType=VARCHAR},'%') 


				and c.company_organization_code like concat('%',#{accountsLog.company.companyOrganizationCode, jdbcType=VARCHAR},'%')  


				and c.company_tax_code like concat('%',#{accountsLog.company.companyTaxCode, jdbcType=VARCHAR},'%') 


				and c.company_zip_code like concat('%',#{accountsLog.company.companyZipCode, jdbcType=VARCHAR},'%')  



					and c.company_type = #{accountsLog.company.companyType.id, jdbcType=INTEGER} 




					and c.company_economy_type = #{accountsLog.company.companyEconomyType.id, jdbcType=INTEGER} 




					and c.company_property = #{accountsLog.company.companyProperty.id, jdbcType=INTEGER} 




					and c.area_code = #{accountsLog.company.area.code, jdbcType=CHAR} 






				and ac.audit_id = #{accountsLog.audit.id,jdbcType=INTEGER}


				and au.year = #{accountsLog.audit.year,jdbcType=CHAR} 




				and ac.audit_process_status = #{accountsLog.auditProcessStatus.id,jdbcType=INTEGER}




			and ac.is_receipt = #{accountsLog.isReceipt, jdbcType=BIT}


			ac.is_active = #{accountsLog.isActive,jdbcType=BIT}



order by ac.year, c.company_code
limit #{start}, #{size}

select count(*) 
from




			ac.year = #{accountsLog.year,jdbcType=CHAR}




				and c.id = #{accountsLog.company.id, jdbcType=INTEGER} 


				and c.company_code like concat('%',#{accountsLog.company.companyCode, jdbcType=VARCHAR},'%')


				and c.company_name like concat('%',#{accountsLog.company.companyName, jdbcType=VARCHAR},'%') 


				and c.company_organization_code like concat('%',#{accountsLog.company.companyOrganizationCode, jdbcType=VARCHAR},'%')  


				and c.company_tax_code like concat('%',#{accountsLog.company.companyTaxCode, jdbcType=VARCHAR},'%') 


				and c.company_zip_code like concat('%',#{accountsLog.company.companyZipCode, jdbcType=VARCHAR},'%')  



					and c.company_type = #{accountsLog.company.companyType.id, jdbcType=INTEGER} 




					and c.company_economy_type = #{accountsLog.company.companyEconomyType.id, jdbcType=INTEGER} 




					and c.company_property = #{accountsLog.company.companyProperty.id, jdbcType=INTEGER} 




					and c.area_code = #{accountsLog.company.area.code, jdbcType=CHAR} 






				and ac.audit_id = #{accountsLog.audit.id,jdbcType=INTEGER}


				and au.year = #{accountsLog.audit.year,jdbcType=CHAR} 




				and ac.audit_process_status = #{accountsLog.auditProcessStatus.id,jdbcType=INTEGER}




			and ac.is_receipt = #{accountsLog.isReceipt, jdbcType=BIT}


			ac.is_active = #{accountsLog.isActive,jdbcType=BIT}
