mapper.select:
select

from audit_company_view 


		c_year = #{year,jdbcType=CHAR} 


		and c_company_code like concat('%', #{companyCode,jdbcType=VARCHAR},'%')


		and c_company_tax_code like concat('%',#{companyTaxCode,jdbcType=VARCHAR},'%')


		and c_company_organization_code like concat('%',#{companyOrganizationCode,jdbcType=VARCHAR},'%')


		and c_company_property = #{companyProperty,jdbcType=INTEGER} 


		and c_company_economy_type = #{companyEconomyType,jdbcType=INTEGER} 


		and a_code like concat ('%', #{areaCode,jdbcType=VARCHAR},'%')  



		and c_company_emp_total >= #{minTotal} 


		and c_company_emp_total <=  #{maxTotal} 


		and c_company_name like concat('%',#{companyName,jdbcType=VARCHAR},'%')


		and c_company_address like concat('%',#{companyAddress,jdbcType=VARCHAR},'%')


		and c_company_legal like concat('%',#{companyLegal,jdbcType=VARCHAR},'%')


		and aup_id = #{auditProcessStatus,jdbcType=INTEGER} 


		and u_id = #{paymentPerson,jdbcType=INTEGER} 


		and au_year <= #{overYear}


		and au_is_exempt = #{isExempt,jdbcType=BIT} 


		and au_actual_amount = #{actualAmount,jdbcType=DECIMAL}


order by c_company_code
limit #{start},#{size}

select count(*) 
from audit_company_view


		c_year = #{year,jdbcType=CHAR} 


		and c_company_code like concat('%', #{companyCode,jdbcType=VARCHAR},'%')


		and c_company_tax_code like concat('%',#{companyTaxCode,jdbcType=VARCHAR},'%')


		and c_company_organization_code like concat('%',#{companyOrganizationCode,jdbcType=VARCHAR},'%')


		and c_company_property = #{companyProperty,jdbcType=INTEGER} 


		and c_company_economy_type = #{companyEconomyType,jdbcType=INTEGER} 


		and a_code like concat ('%', #{areaCode,jdbcType=VARCHAR},'%')  



		and c_company_emp_total >= #{minTotal} 


		and c_company_emp_total <=  #{maxTotal} 


		and c_company_name like concat('%',#{companyName,jdbcType=VARCHAR},'%')


		and c_company_address like concat('%',#{companyAddress,jdbcType=VARCHAR},'%')


		and c_company_legal like concat('%',#{companyLegal,jdbcType=VARCHAR},'%')


		and aup_id = #{auditProcessStatus,jdbcType=INTEGER} 


		and u_id = #{paymentPerson,jdbcType=INTEGER} 


		and au_year <= #{overYear}


		and au_is_exempt = #{isExempt,jdbcType=BIT} 


		and au_actual_amount = #{actualAmount,jdbcType=DECIMAL}

select

from audit_company_view 

	au_year = #{year,jdbcType=CHAR}

		and t_id = #{companyType,jdbcType=INTEGER}


		and et_id = #{companyEconomyType,jdbcType=INTEGER} 


		and a_code  = #{areaCode,jdbcType=CHAR}

select count(*) as num, aup_id from audit_company_view 

	au_year = #{year,jdbcType=CHAR}

		and t_id = #{companyType,jdbcType=INTEGER}


		and et_id = #{companyEconomyType,jdbcType=INTEGER} 


		and a_code  = #{areaCode,jdbcType=CHAR}


group by aup_id

