mapper.select:
select

from





			and c.id = #{companyLog.id, jdbcType=INTEGER},


			and c.company_code like concat('%', #{companyLog.companyCode,jdbcType=VARCHAR},'%')


			and c.company_name like concat('%',#{companyLog.companyName,jdbcType=VARCHAR},'%')



				and c.company_type = #{companyLog.companyType.id,jdbcType=INTEGER}




				and c.company_economy_type = #{companyLog.companyEconomyType.id,jdbcType=INTEGER}




				and c.company_property = #{companyLog.companyProperty.id,jdbcType=INTEGER}




				and c.area_code = #{companyLog.area.code,jdbcType=VARCHAR} 



			and c.company_tax_code like concat('%',#{companyLog.companyTaxCode,jdbcType=VARCHAR},'%')


			and c.company_legal like concat('%',#{companyLog.companyLegal,jdbcType=VARCHAR},'%')


			and c.company_organization_code like concat('%',#{companyLog.companyOrganizationCode,jdbcType=VARCHAR},'%')


			and c.company_address like concat('%',#{companyLog.companyAddress},'%')


			and c.user_id = #{companyLog.userId,jdbcType=INTEGER}



order by c.company_code
limit #{start},#{size}

select count(*)
from





			and c.id = #{companyLog.id, jdbcType=INTEGER},


			and c.company_code like concat('%', #{companyLog.companyCode,jdbcType=VARCHAR},'%')


			and c.company_name like concat('%',#{companyLog.companyName,jdbcType=VARCHAR},'%')



				and c.company_type = #{companyLog.companyType.id,jdbcType=INTEGER}




				and c.company_economy_type = #{companyLog.companyEconomyType.id,jdbcType=INTEGER}




				and c.company_property = #{companyLog.companyProperty.id,jdbcType=INTEGER}




				and c.area_code = #{companyLog.area.code,jdbcType=VARCHAR} 



			and c.company_tax_code like concat('%',#{companyLog.companyTaxCode,jdbcType=VARCHAR},'%')


			and c.company_legal like concat('%',#{companyLog.companyLegal,jdbcType=VARCHAR},'%')


			and c.company_organization_code like concat('%',#{companyLog.companyOrganizationCode,jdbcType=VARCHAR},'%')


			and c.company_address like concat('%',#{companyLog.companyAddress},'%')


			and c.user_id = #{companyLog.userId,jdbcType=INTEGER}
