		and w_worker_birth_year <= #{minBirth} 


		and w_worker_birth_year >= #{maxBirth} 


		and w_phone like concat('%', #{phone},'%')


		and t_id = #{workerHandicapType}


		and l_id = #{workerHandicapLevel}

