select 

from company_worker_view 


		c_id = #{companyId} 


		and c_year = #{year,jdbcType=VARCHAR}


		and c_company_code = #{companyCode,jdbcType=VARCHAR}



		and (w_worker_birth_year <= #{maxMaleBirth} and w_worker_gender = 1)  


		or (w_worker_birth_year <= #{maxFemaleBirth} and w_worker_gender = 0)


order by w_worker_name desc 
limit #{start},#{size}

