mapper.select:
select

from permission_type



			permission_type like concat('%',#{permissionType.permissionType, jdbcType=VARCHAR}, '%') 


			and start_time = #{permissionType.startTime, jdbcType=VARCHAR}


			and end_time = #{permissionType.endTime, jdbcType=VARCHAR}


			and user_id = #{permissionType.userId, jdbcType=INTEGER}


			and is_active = #{permissionType.isActive, jdbcType=BIT}



				and is_active = #{permissionType.isActive, jdbcType=BIT}


				and is_active = 0









			and is_active = 0



order by update_time asc 
limit #{start}, #{size}

select count(*) 
from permission_type



			permission_type like concat('%',#{permissionType.permissionType, jdbcType=VARCHAR}, '%') 


			and start_time = #{permissionType.startTime, jdbcType=VARCHAR}


			and end_time = #{permissionType.endTime, jdbcType=VARCHAR}


			and user_id = #{permissionType.userId, jdbcType=INTEGER}


			and is_active = #{permissionType.isActive, jdbcType=BIT}



				and is_active = #{permissionType.isActive, jdbcType=BIT}


				and is_active = 0









			and is_active = 0
