mapper.select:
select 	count(*)
	from
	(select year, 
		company_code, 
		worker_id
		from 
		hesf.company_year_worker
		where company_code = #{companyCode} and year= #{year} 
	) as a ,worker as b 
where a.worker_id=b.id and b.worker_handicap_type = #{workerHandicapType} and b.worker_handicap_level = #{workerHandicapLevel} ;
