mapper.select:
select

from





			and w.id = #{workerLog.id, jdbcType=INTEGER} 


			and w.worker_name like
			concat('%',#{workerLog.workerName,jdbcType=VARCHAR},'%')


			and w.worker_gender = #{workerLog.workerGender,jdbcType=VARCHAR} 


			and w.worker_birth = #{workerLog.workerBirth,jdbcType=VARCHAR}


			and w.phone like concat('%',#{workerLog.phone,jdbcType=VARCHAR},'%')


			and w.worker_id_card like concat('%',
			#{workerLog.workerIdCard,jdbcType=INTEGER},'%')


			and w.worker_handicap_code like concat('%',
			#{workerLog.workerHandicapCode,jdbcType=VARCHAR},'%')



				and w.worker_handicap_type =
				#{workerLog.workerHandicapType.id,jdbcType=INTEGER} 




				and w.worker_handicap_level =
				#{workerLog.workerHandicapLevel.id,jdbcType=INTEGER} 




				and w.area_code like concat('%',#{workerLog.area.code,jdbcType=VARCHAR},'%')  



			and w.is_college =  #{workerLog.isCollege,jdbcType=BIT} 


			and w.is_retired  = #{workerLog.isRetired,jdbcType=BIT} 


			and w.is_cadre  = #{workerLog.isCadre,jdbcType=BIT} 


			and w.is_professor = #{workerLog.isProfessor,jdbcType=BIT}


			and w.user_id = #{workerLog.userId,jdbcType=INTEGER} 


			and w.is_active = #{workerLog.isActive,jdbcType=BIT} 



order by w.id desc
limit #{start}, #{size}

select count(*)
from





			and w.id = #{workerLog.id, jdbcType=INTEGER} 


			and w.worker_name like
			concat('%',#{workerLog.workerName,jdbcType=VARCHAR},'%')


			and w.worker_gender = #{workerLog.workerGender,jdbcType=VARCHAR} 


			and w.worker_birth = #{workerLog.workerBirth,jdbcType=VARCHAR}


			and w.phone like concat('%',#{workerLog.phone,jdbcType=VARCHAR},'%')


			and w.worker_id_card like concat('%',
			#{workerLog.workerIdCard,jdbcType=INTEGER},'%')


			and w.worker_handicap_code like concat('%',
			#{workerLog.workerHandicapCode,jdbcType=VARCHAR},'%')



				and w.worker_handicap_type =
				#{workerLog.workerHandicapType.id,jdbcType=INTEGER} 




				and w.worker_handicap_level =
				#{workerLog.workerHandicapLevel.id,jdbcType=INTEGER} 




				and w.area_code like concat('%',#{workerLog.area.code,jdbcType=VARCHAR},'%')  



			and w.is_college =  #{workerLog.isCollege,jdbcType=BIT} 


			and w.is_retired  = #{workerLog.isRetired,jdbcType=BIT} 


			and w.is_cadre  = #{workerLog.isCadre,jdbcType=BIT} 


			and w.is_professor = #{workerLog.isProfessor,jdbcType=BIT}


			and w.user_id = #{workerLog.userId,jdbcType=INTEGER} 


			and w.is_active = #{workerLog.isActive,jdbcType=BIT}
